
public class Quotient {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int numu=49;
int deno=7;
int quoti=0;
while(numu>=deno){
	

	numu=numu-deno;
quoti++;

	}
System.out.println("quotient is"+quoti);
System.out.println("remainder is"+numu );
	}
	

}
